import List from "./List";
import Form from "./Form";
import {} from "./styles.css";

function Contacts() {
  const [contacts, setContacts] = useState(() => {
    const savedContacts = localStorage.getItem("contacts");
    return savedContacts
      ? JSON.parse(savedContacts)
